mapper.select:

SELECT notice_seq,category,title,writer_id,content,count,like_it,reg_date,update_date
	FROM
		(
			SELECT /*+INDEX_DESC(table_board pk_notice)*/
				ROWNUM rn,notice_seq,category,title,writer_id,content,count,like_it,reg_date,update_date
				FROM table_notice
			WHERE ROWNUM<=#{pageNumber}*#{amount}
		)
WHERE rn>(#{pageNumber}-1)*#{amount}
